#include "CircularBuffer.h"
#include <stdlib.h>


struct CircularBuffer
{
    // keep compiler from complaining about an empty struct
    int *piValue;
    unsigned int uiIn;
    unsigned int uiOut;
    unsigned int uiCapacity;
    unsigned int uiFifoSize;
    int iDefValue;
};

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{
    struct CircularBuffer * self = (struct CircularBuffer *)calloc(1, sizeof(struct CircularBuffer));
    if(NULL == self)
    {
        return self;
    }
    else
    {
        self->iDefValue = default_value;
        self->uiOut = 0;
        self->piValue = (int *)calloc(capacity, sizeof(int));
        self->uiCapacity = capacity;

        for(self->uiIn = 0; self->uiIn < self->uiCapacity; self->uiIn++)
            self->piValue[self->uiIn] = default_value;
        self->uiIn = 0;
        self->uiFifoSize = 0;
    }
    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * self)
{
    free(self->piValue);
    free(self);
}

bool CircularBuffer_IsEmpty(struct CircularBuffer *s)
{
    if(s)
    {
        if(!s->uiFifoSize)
            return 1;
    }

    return 0;
}

bool CircularBuffer_IsFull(struct CircularBuffer *s)
{
    if(s->uiFifoSize == s->uiCapacity)
        return 1;
    else
        return 0;
}

/** Putting to a full CircularBuffer
  * returns false
  * loses no prior values
  */
bool CircularBuffer_Put(struct CircularBuffer *s, int iPutValue)
{
//    if(s)
    {
        if(s->uiFifoSize == s->uiCapacity)
            return 0;

        s->piValue[s->uiIn] = iPutValue;

        s->uiFifoSize++;

        s->uiIn++;

        s->uiIn = s->uiIn % s->uiCapacity;      

        return 1;
    }
}

/* Getting from an empty CircularBuffer returns a
  default value provided during create function.
  */
int CircularBuffer_Get(struct CircularBuffer *s)
{
    int iRetVal = 0;
    //if(s)
    {
        if(!s->uiFifoSize)
            return s->iDefValue;

        iRetVal = s->piValue[s->uiOut];
        s->uiOut++;
        s->uiOut = s->uiOut % (s->uiCapacity);
        s->uiFifoSize--;

        return iRetVal;
    }
}

unsigned int CircularBuffer_Capacity(struct CircularBuffer *s)
{
    return s->uiFifoSize;
}

