#include "CircularBuffer.h"

/* Changes i would do:
   use typedef to define struct type
   use typdef to define element type, easier to change later, but the prototypes are fixed
   use the return value of each API as success/error indicator and pass the struct
       as context(pointer to it)
*/

/* Changes/Assumptions I did:
  No checks are done on the malloc return values, as agreed 
  Use malloc instead of calloc, since i will initialize anyway the struct
*/

typedef int t_BufferElement; //--> belongs to the header

struct CircularBuffer
{
    unsigned int  size;
    unsigned int  wIndex;
    unsigned int  rIndex;
    int           *pBuffer;
};

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{

    struct CircularBuffer * self = (struct CircularBuffer *)malloc(sizeof(struct CircularBuffer));

    self->size    = capacity;
    self->wIndex  = 0;
    self->rIndex  = 0;
    self->pBuffer = (int*)malloc(sizeof(int)/sizeof(unsigned char)*capacity);

    for(unsigned int i=0; i<capacity; i++)
    {
        self->pBuffer[i] = default_value;
    }

    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * self)
{
    free(self->pBuffer);
    free(self);
}

