#include "CircularBuffer.h"

/* Changes i would do:
   use typedef to define struct type
   use typdef to define element type, easier to change later, but the prototypes are fixed
   use the return value of each API as success/error indicator and pass the struct
       as context(pointer to it)
*/

/* Utility macros*/
//#define GET_FREE_SPACE(size,wPos,rPos) ( (wPos==rPos)?(size):((size-(wPos-rPos))%(size)) )
//#define GET_USED_SPACE(size,wPos,rPos) ( (wPos==rPos)?(size):((size-(wPos-rPos))%(size)) )
#define GET_ABS_POS_DIST(size,wPos,rPos) ( (wPos==rPos)?(size):((size-(wPos-rPos))%(size)) )
#define WRAP_INC_POS(size,pos) pos = (pos+1)%(size)

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{    
    struct CircularBuffer * pSelf = (struct CircularBuffer *)malloc(sizeof(struct CircularBuffer));

    pSelf->size     = capacity;
    pSelf->wPos     = 0;
    pSelf->rPos     = 0;
    pSelf->fullFlag = false;
    pSelf->emptyFlag= true;
    pSelf->initVal  = default_value;
    pSelf->pBuffer  = (int*)malloc(sizeof(int)/sizeof(unsigned char)*capacity);

    for(unsigned int i=0; i<capacity; i++)
    {
        pSelf->pBuffer[i] = default_value;
    }

    return pSelf;
}//CircularBuffer_Create(

void CircularBuffer_Destroy(struct CircularBuffer * pSelf)
{
    free(pSelf->pBuffer);
    free(pSelf);
}

unsigned int CircularBuffer_Capacity(struct CircularBuffer * pSelf)
{
    return pSelf->size;
}

bool CircularBuffer_Put(struct CircularBuffer * pSelf, int inValue)
{
    if( (GET_ABS_POS_DIST(pSelf->size,pSelf->wPos,pSelf->rPos) == 0) ||
        (pSelf->fullFlag) )
    {
        //in case its full, return false
        return false;
    }   
    else
    {
        //insert element
        pSelf->pBuffer[pSelf->wPos] = inValue;
        pSelf->emptyFlag = false;

        WRAP_INC_POS(pSelf->size,pSelf->wPos);
        if(pSelf->rPos == pSelf->wPos)
            pSelf->fullFlag = true;

        return true;
    }
}//bool CircularBuffer_Put(

int CircularBuffer_Get(struct CircularBuffer * pSelf)
{
    if( (GET_ABS_POS_DIST(pSelf->size,pSelf->wPos,pSelf->rPos) == 0) ||
        (pSelf->emptyFlag) )
    {
        //buffer empty, return initvalue
        return pSelf->initVal;
    }
    else
    {
        int retVal = pSelf->pBuffer[pSelf->rPos];
        pSelf->fullFlag = false;

        WRAP_INC_POS(pSelf->size,pSelf->rPos);
        if(pSelf->rPos == pSelf->wPos)
            pSelf->emptyFlag = true;

        return retVal;
    }
}//int CircularBuffer_Get(

