TEST_GROUP(CircularBuffer)
{
    CircularBuffer * pElementUnderTest;
    unsigned int capacity = 10;
    int empty_value = -1;

    int inStartValue = 0;
    int inStepValue  = 2;


    // setup runs before each test
    void setup()
    {
        pElementUnderTest = CircularBuffer_Create(capacity, empty_value);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(pElementUnderTest);
    }
};

// PUSH THE TEST BUTTON WHEN YOU START TO WORK
// Pushing the test button saves your work and runs the build.

// each test gets a fresh CircularBuffer
TEST(CircularBuffer, create_destroy)
{
    //FAIL("Start here");
unsigned int i=0; //used for index
unsigned int tmp; //general purpose temporary var
         int buff=0; //general buffer storage
 int testStartVal; 


/*Positive tests(expected to pass)*/

    //Sanity boundary test, parse trough the complete array
    for(i=0;i<capacity;i++)
    {
        LONGS_EQUAL(pElementUnderTest->pBuffer[i], empty_value);
    }  

/* Interface (API) tests */
    //Check the _Get for empty indication, expecting the empty value
    LONGS_EQUAL(CircularBuffer_Get(pElementUnderTest), empty_value);

    //Fill the buffer with the _Put, expecting true
    for(i=0;i<capacity;i++)
    {
        printf("Running the Fill_Up_Pos_Test, wPos: %i , putValue: %i ",\
                                              pElementUnderTest->wPos ,\
                                              inStartValue+i         );

        CHECK_TRUE(CircularBuffer_Put(pElementUnderTest,inStartValue+i));
        printf(" -- Test iteration Pass\n");
    }   

    //Check the fullness, with one extra _put
    printf("Running the Fill_Up_Neg_Test, wPos: %i \n\n",pElementUnderTest->wPos);
    CHECK_FALSE(CircularBuffer_Put(pElementUnderTest,capacity));

    //Empty the buffer with the _Get, expecting the sequantially written in values
    for(i=0;i<capacity;i++)
    {         
        printf("Running the Empty_Pos_Test, rPos: %i, expVal: %i ",\
                                                  pElementUnderTest->rPos,     \
                                                  inStartValue+i);

        buff = CircularBuffer_Get(pElementUnderTest);
        LONGS_EQUAL((inStartValue+i),buff);
        printf(" -- Test iteration Pass\n");
    }   

    //Check the emptyness, with one extra _get
    printf("Running the Empty_Neg_Test, rPos: %i \n\n",pElementUnderTest->rPos);
    buff = CircularBuffer_Get(pElementUnderTest);
    LONGS_EQUAL(empty_value,buff);

    //1+1 W_Follow_me test, the read position follows the write position

    tmp = capacity*4+1; //to have an odd number
    printf("Running the W_Follow_Me_Test ");
    for(i=0;i<tmp;i++)
    {   
        testStartVal = inStartValue+(i*inStepValue);

        printf(" - wPos: %i , putValue: %i ",\
                   pElementUnderTest->wPos  ,\
                   testStartVal         );

        CHECK_TRUE(CircularBuffer_Put(pElementUnderTest,testStartVal));
        printf(" -- Test W Step Pass");

        printf(" - rPos: %i, expVal: %i " , \
                   pElementUnderTest->rPos, \
                   testStartVal);

        buff = CircularBuffer_Get(pElementUnderTest);
        LONGS_EQUAL(testStartVal,buff);
        printf(" -- Test R Pass");

        printf(" >> Test iteration Pass\n");
    }   

    //Check the emptyness, with one extra _get
    printf("Running the Empty_Neg_Test, rPos: %i \n\n",pElementUnderTest->rPos);
    buff = CircularBuffer_Get(pElementUnderTest);
    LONGS_EQUAL(empty_value,buff);

    //1+1 R_Follow_me test, the write position follows the read position
    testStartVal = inStartValue+inStepValue;
    //put 1 test value in
    CHECK_TRUE(CircularBuffer_Put(pElementUnderTest,testStartVal));

    tmp = capacity*4+1; //to have an odd number
    printf("Running the R_Follow_Me_Test ");
    for(i=0;i<tmp;i++)
    {                
        printf(" - rPos: %i",pElementUnderTest->rPos);

        /*checking the read out value is not possible, since we are reading before
