#include "CircularBuffer.h"

CircularBuffer::CircularBuffer(const unsigned int capacity, const int defaultValue)
  : values_ { capacity, defaultValue },
    capacity_ { capacity },
    defaultValue_ { defaultValue },
    get_index_ { 0 },
    put_index_ { 0 }
{
    if (capacity == 0)
        throw CircularBufferException { "capacity cannot be zero" };
}

CircularBuffer::~CircularBuffer()
{
}

bool
CircularBuffer::isEmpty() const
{
    return get_index_ == put_index_;
}

bool
CircularBuffer::isFull() const
{
    return get_index_ + capacity_ == put_index_;
}

void
CircularBuffer::put(int value)
{
    if (isFull())
        throw CircularBufferException { "buffer full" };

    values_[put_index_++ % capacity_] = value;
}

int
CircularBuffer::get()
{
    if (isEmpty())
        return defaultValue_;

    return values_[get_index++ % capacity_];
}

int
CircularBuffer::capacity() const
{
    return capacity_;
}

