//- Copyright (c) 2018 James Grenning -- See license.txt at https://github.com/jwgrenning/wingman-cyber-dojo

#include "CppUTest/TestHarness.h"
#include "CircularBuffer.h"

TEST_GROUP(CircularBuffer)
{
    CircularBuffer* buffer = nullptr;
    const unsigned int capacity = 10;
    const int emptyValue = -1;
    const int someValue = 42;

    void fillBuffer()
    {
        for (unsigned int i = 0; i < capacity; i++)
            buffer->put(i);
    }

    void setup()
    {
        buffer = new CircularBuffer(capacity, emptyValue);
    }

    void teardown()
    {
        delete buffer;
    }

};

TEST(CircularBuffer, ShouldBeEmptyByDefault)
{
    CHECK_TRUE(buffer->isEmpty());
}

TEST(CircularBuffer, ShouldNotBeFullByDefault)
{
    CHECK_FALSE(buffer->isFull());
}

TEST(CircularBuffer, ShouldReturnDefaultValueOnGet)
{
    LONGS_EQUAL(emptyValue, buffer->get());
}

TEST(CircularBuffer, ShouldRememberInitialCapacity)
{
    LONGS_EQUAL(capacity, buffer->capacity());
}

TEST(CircularBuffer, ShouldNotAllowZeroCapacity)
{
    try {
        CircularBuffer illegal { 0, emptyValue };
        FAIL("should not be allowed to set capacity to zero");
    } catch (CircularBufferException& e) {
        CHECK_EQUAL(std::string { "capacity cannot be zero" }, e.message());
    }
}

TEST(CircularBuffer, ShouldNotBeEmptyAfterPut)
{
    buffer->put(someValue);

    CHECK_FALSE(buffer->isEmpty());
}

TEST(CircularBuffer, ShouldGetValuesInSameOrderAsTheyWerePut)
{
    buffer->put(1);
    buffer->put(2);
    buffer->put(3);

    LONGS_EQUAL(1, buffer->get());
    LONGS_EQUAL(2, buffer->get());
    LONGS_EQUAL(3, buffer->get());
}

TEST(CircularBuffer, ShouldReturnValuesInSameOrderAsOriginallyPutWhenInterleavingPutAndGet)
{
    buffer->put(1);
    buffer->put(2);
    buffer->put(3);

    LONGS_EQUAL(1, buffer->get());

    buffer->put(4);

    LONGS_EQUAL(2, buffer->get());

    buffer->put(5);

    LONGS_EQUAL(3, buffer->get());

    buffer->put(6);

    LONGS_EQUAL(4, buffer->get());
    LONGS_EQUAL(5, buffer->get());
    LONGS_EQUAL(6, buffer->get());
}

TEST(CircularBuffer, ShouldBeFullAfterPuttingValuesCapacityTimesWithNoGet)
{
    fillBuffer();

    CHECK_TRUE(buffer->isFull());
}

TEST_GROUP(FullCircularBuffer)
{
    CircularBuffer* buffer = nullptr;
    const unsigned int capacity = 10;
    const int emptyValue = -1;
    const int someValue = 42;

    void drainBuffer()
    {
        for (unsigned int i = 0; i < capacity; i++)
            buffer->get();
    }

    void fillBuffer()
    {
        for (unsigned int i = 0; i < capacity; i++)
            buffer->put(i);
    }

    void setup()
    {
        buffer = new CircularBuffer(capacity, emptyValue);
        fillBuffer();
    }

    void teardown()
    {
        delete buffer;
    }

};

TEST(FullCircularBuffer, ShouldNotBeEmpty)
{
    CHECK_FALSE(buffer->isEmpty());
}

TEST(FullCircularBuffer, ShouldThrowCircularBufferExceptionOnPut)
{
    try {
        buffer->put(someValue);
        FAIL("should not be able to put more values when buffer is full");
    } catch (CircularBufferException& e) {
        CHECK_EQUAL(std::string { "buffer full" }, e.message());
    }
}

TEST(FullCircularBuffer, ShouldBeAbleToPutAgainAfterGet)
{
    buffer->get();

    try {
        buffer->put(someValue);
    } catch (CircularBufferException& e) {
        FAIL(e.message().c_str());
    }
}

TEST(FullCircularBuffer, ShouldNotBeFullAfterGet)
{
    buffer->get();

    CHECK_FALSE(buffer->isFull());
}

TEST(FullCircularBuffer, ShouldBeEmptyAgainAfterGettingEveryValue)
{
    drainBuffer();

    CHECK_TRUE(buffer->isEmpty());
}

