#include "CircularBuffer.h"
#include <string.h>

struct CircularBuffer
{
    unsigned int write_index;
    unsigned int read_index;
    unsigned int size;
    int default_value;
    int * buffer;
};

static void update_index(
    unsigned int * const p_index,
    unsigned int delta,
    unsigned int size);

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{
    struct CircularBuffer * self = (struct CircularBuffer *)calloc(1, sizeof(struct CircularBuffer));
    memset(self, 0x00, sizeof(struct CircularBuffer));
    self->size = capacity;
    self->default_value = default_value;
    self->buffer = (int *)calloc(self->size, sizeof(int));

    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * const self)
{
    free(self->buffer);
    free(self);
}

bool CircularBuffer_IsEmpty(struct CircularBuffer * const self)
{
    unsigned int read = self->read_index;
    unsigned int write = self->write_index;

    return ((read == write) ? true : false);
}

bool CircularBuffer_IsFull(struct CircularBuffer * const self)
{
    unsigned int read = self->read_index;
    unsigned int write = self->write_index;
    unsigned int size = self->size;

    return ((((write + 1) % size) == read) ? true : false);
}

bool CircularBuffer_Put(struct CircularBuffer * const self, int value)
{
    bool ret_value = false;

    if (false == CircularBuffer_IsFull(self))
    {
        self->buffer[self->write_index] = value;
        update_index(&self->write_index, 1, self->size);
        ret_value = true;
    }

    return ret_value;
}

int CircularBuffer_Get(struct CircularBuffer * const self)
{
    unsigned int ret_value = self->default_value;

    if (false == CircularBuffer_IsEmpty(self))
    {
        ret_value = self->buffer[self->read_index];
        update_index(&self->read_index, 1, self->size);
    }

    return (ret_value);
}

unsigned int CircularBuffer_Capacity(struct CircularBuffer * const self)
{
    unsigned int size = self->size;

    return (size);
}

static void update_index(
    unsigned int * const p_index,
    unsigned int delta,
    unsigned int size)
{
    *p_index += delta;

    while (*p_index >= size)
    {
        *p_index -= size;
    }
}

