#include "CppUTest/TestHarness.h"

extern "C"
{
    #include "CircularBuffer.h"
}


// Everything in the test group is available
// to associated test cases
TEST_GROUP(CircularBuffer)
{
    CircularBuffer* buffer = 0;
    const unsigned int capacity = 10;
    const int emptyValue = -1;

    // setup runs before each test
    void setup()
    {
        buffer = CircularBuffer_Create(capacity, emptyValue);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buffer);
    }

};

// PUSH THE TEST BUTTON WHEN YOU START TO WORK
// Pushing the test button saves your work and runs the build.

// each test gets a fresh CurcularBuffer
TEST(CircularBuffer, Created)
{
    LONGS_EQUAL(capacity, CircularBuffer_Capacity(buffer));
    CHECK_FALSE(CircularBuffer_IsFull(buffer));
    CHECK_TRUE(CircularBuffer_IsEmpty(buffer));
    LONGS_EQUAL(emptyValue, CircularBuffer_Get(buffer));
}

TEST(CircularBuffer, add_one_item_and_remove_it)
{
    int value = 10;

    CHECK_TRUE(CircularBuffer_IsEmpty(buffer));
    CHECK_TRUE(CircularBuffer_Put(buffer, value));
    CHECK_FALSE(CircularBuffer_IsEmpty(buffer));
    CHECK_EQUAL(value, CircularBuffer_Get(buffer));
    CHECK_TRUE(CircularBuffer_IsEmpty(buffer));
}

TEST(CircularBuffer, fill_up_the_buffer)
{
    int inc_val = 0;

    CHECK_TRUE(CircularBuffer_IsEmpty(buffer));

    while (false == CircularBuffer_IsFull(buffer))
    {
        CHECK_TRUE(CircularBuffer_Put(buffer, ++inc_val));
    }

    CHECK_TRUE(CircularBuffer_IsFull(buffer));
    CHECK_FALSE(CircularBuffer_IsEmpty(buffer));
}

