#include "CircularBuffer.h"
#include "stdint.h"
#include "stdlib.h"

struct CircularBuffer
{
    unsigned int head;
    unsigned int tail;
    const unsigned int capacity;
    unsigned int length;
    int data[];
};

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, 
                                              int default_value)
{  
    struct CircularBuffer * self = (struct CircularBuffer*)
        calloc(1, 
               offsetof(struct CircularBuffer, data) + 
               capacity * sizeof(self->data[0])
              );

    for(unsigned int idx = 0u; idx < capacity; ++idx)
    {
        self->data[idx] = default_value;
    }

    // Intentionally casting away const to initialize this const member.
    // Capacity should never change again for the duration of the life of
    // this object.
    *(unsigned int *) &self->capacity = capacity;

    self->head = 0u;
    self->tail = 0u;

    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * self)
{
    free(self);
}

int CircularBuffer_Get(struct CircularBuffer * p_buffer)
{
    int value = p_buffer->data[p_buffer->tail];
    p_buffer->tail++;
    return value;
}

bool CircularBuffer_Put(struct CircularBuffer * p_buffer, int value)
{
    p_buffer->data[p_buffer->head] = value;
    p_buffer->length++;
    p_buffer->head++;
    return true;
}

unsigned int CircularBuffer_Capacity(struct CircularBuffer * p_buffer)
{
    return p_buffer->capacity;
}


bool CircularBuffer_IsFull(struct CircularBuffer * p_buffer)
{
    return  (p_buffer->length == p_buffer->capacity) ? true : false;
}

bool CircularBuffer_IsEmpty(struct CircularBuffer * p_buffer)
{
    return true;
}

