#include "CppUTest/TestHarness.h"

extern "C"
{
    #include "CircularBuffer.h"
}

//Helper function for loading up a buffer with a sequence of numbers starting
//with 0 through entries-1.
static inline bool fill_buffer(struct CircularBuffer *p_buffer,
                               unsigned int entries)
{
    bool b_ret = true;
    unsigned int idx = 0u;

    while((idx < entries) && b_ret)
    {
        b_ret = CircularBuffer_Put(p_buffer, idx);
        ++idx;
    }

    return b_ret;
}


// Everything in the test group is available
// to associated test cases
//
// Test Cases
// 1. Create a buffer
// 2. Read a value from an empty buffer
// 3. Insert a value into an empty buffer.
// 4. Read a value from a buffer.
// 5. Read buffer capacity
// 6. Insert values into the buffer until it is full.
// 7. Read values out of the buffer until it is empty.
// 8. Insert into a full buffer.
// 9. Repeat reading from an empty buffer.
// Todo: Consider how to test wrap around scenarios

TEST_GROUP(CircularBuffer)
{
    CircularBuffer* buffer = 0;

    const unsigned int capacity = 10;
    const int emptyValue = -1;

    // setup runs before each test
    void setup()
    {
        buffer = CircularBuffer_Create(capacity, emptyValue);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buffer);
    }

};

// PUSH THE TEST BUTTON WHEN YOU START TO WORK
// Pushing the test button saves your work and runs the build.

/* 
* Test Case 1: Create a buffer
*/
TEST(CircularBuffer, create)
{
    // The reqruirements for this exercise said do not worry about calloc/malloc
    // failures. But I am going to first check that the pointer returned by
    // calloc is not null.
    CHECK(NULL != buffer);
}

/*
* Test Case 2. Read a value from an empty buffer
*
*           Checking that a buffer was properly initialized
* I have no way of accessing the data in the array without a complete
* declaration of the struct, but the rules of this exercise do not allow
* me to modify the incomple type found in the header with a complete
* declaration. However the requirements state that:
*  
*    " * Getting from an empty CircularBuffer returns a
*        default value provided during create function."
*
* Therefore this test tests whether or not the buffer's internal array
* was properly initialized.
*/
TEST(CircularBuffer, read_value_from_empty_buffer)
{
    CHECK(emptyValue == CircularBuffer_Get(buffer));
}

/*
* Test Case 3: Insert a value into an empty buffer.
*/
TEST(CircularBuffer, read_value_from_buffer)
{
    //Test Case 2: Read a value from the buffer.
    static const int foo = 100;
    CircularBuffer_Put(buffer, foo);
    CHECK(CircularBuffer_Get(buffer) == foo);

}

// Test Case 4. Read a value from a buffer.
TEST(CircularBuffer, insert_into_empty_buffer)
{    
    static const int foo = 100;
    CHECK_TRUE(CircularBuffer_Put(buffer, foo));
}


// Test Case 5: Read buffer capacity
// Check 1 - Check the initial capacity of the buffer
// Check 2 - Create a buffer with a different capacity and read the new
//           capacity back.
TEST(CircularBuffer, read_buffer_capacity)
{
    CHECK(capacity == CircularBuffer_Capacity(buffer));

    CircularBuffer_Destroy(buffer);

    static const unsigned int new_size = 5u; 
    buffer = CircularBuffer_Create(new_size, emptyValue);

    CHECK(new_size == CircularBuffer_Capacity(buffer));
}



// Test Case 6: Insert values into the buffer until it is full.
TEST(CircularBuffer, insert_values_until_full)
{
    fill_buffer(buffer, capacity);

    CHECK_TRUE(CircularBuffer_IsFull(buffer));
}

// 7. Read values out of the buffer until it is empty.
// Check 1 - Load the buffer up, and read it back, verify the values are what
//           we expect them to be.
// Check 2 - Check that the buffer reports that it is empty.
TEST(CircularBuffer, read_values_until_empty)
{
    fill_buffer(buffer, capacity);

    for(unsigned int idx = 0u; idx < capacity; idx++)
    {
        CHECK(idx == (unsigned) CircularBuffer_Get(buffer));
    }

    CHECK_TRUE(CircularBuffer_IsEmpty(buffer));


}



