#include "CircularBuffer.h"

struct CircularBuffer
{
    int buffer[1024];
    unsigned int head;
    unsigned int tail;
    bool buf_empty;
    unsigned int buf_size;
    int def_val;
};

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{
    struct CircularBuffer * self = (struct CircularBuffer *)calloc(1, sizeof(struct CircularBuffer));

    // Init circular buffer
    self->head = 0;
    self->tail = 0;
    self->buf_empty = true;
    self->buf_size = capacity;
    self->def_val = default_value;

    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * self)
{
    free(self);
}


// PUSH THE TEST BUTTON WHEN YOU START TO WORK
// Pushing the test button saves your work and runs the build.



bool CircularBuffer_IsEmpty(struct CircularBuffer * cb)
{
    if((cb->tail == cb->head) && cb->buf_empty)
    {
        return true;
    }

    else
    {
        return false;
    }
}

bool CircularBuffer_IsFull(struct CircularBuffer * cb)
{
    if((cb->tail == cb->head) && !cb->buf_empty)
    {
        return true;
    }

    else
    {
        return false;
    }   
}

bool CircularBuffer_Put(struct CircularBuffer * cb, int data)
{
    // Check if buffer already full
    if(CircularBuffer_IsFull(cb))
    {
        // Full so do not add data
        return false;
    }


    // Put data in buffer
    cb->buffer[cb->head] = data;

    // Advance head
    cb->head++;

    //Check/handle head moved past buffer space    
    if(cb->head >= (cb->buf_size))
    {
        cb->head = 0;
    }   

    // Buffer not empty
    cb->buf_empty = false;

    return true;

}

int CircularBuffer_Get(struct CircularBuffer * cb)
{
    int data;

    // Check if buffer is empty
    if(CircularBuffer_IsEmpty(cb))
    {
        return cb->def_val;   
    }

    else  // Buffer has data
    {
        data = cb->buffer[cb->tail];

        cb->tail++;

        if(cb->tail >= (cb->buf_size))
        {
            cb->tail = 0;
        }        
    }

    return data;
}

unsigned int CircularBuffer_Capacity(struct CircularBuffer * cb)
{
    unsigned int count;

    if(cb->head >= cb->tail)
    {
        count = (cb->buf_size - (cb->head - cb->tail));   
    }

    else // (cb->head < cb->tail)
    {
        count = (cb->buf_size - (cb->tail - cb->head)); 
    }

    return count;
}

