TEST_GROUP(CircularBuffer)
{
    CircularBuffer* buffer = 0;
    const unsigned int capacity = 10;
    const int emptyValue = -1;

    // setup runs before each test
    void setup()
    {
        buffer = CircularBuffer_Create(capacity, emptyValue);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buffer);
    }

};

// PUSH THE TEST BUTTON WHEN YOU START TO WORK
// Pushing the test button saves your work and runs the build.

// each test gets a fresh CurcularBuffer
TEST(CircularBuffer, check_empty_then_not_empty)
{
    bool bTestResult;

    // Should be empty
    bTestResult = CircularBuffer_IsEmpty(buffer);
    CHECK_TRUE(bTestResult);

    // Add data and should no longer be empty
    CircularBuffer_Put(buffer, 0x1234); 
    bTestResult = CircularBuffer_IsEmpty(buffer);
    CHECK_FALSE(bTestResult);
}


TEST(CircularBuffer, def_val_check)
{
    int data;

    // Should be empty
    data = CircularBuffer_Get(buffer);
    LONGS_EQUAL(data, -1);
}


TEST(CircularBuffer, check_capacity_when_putting_and_getting)
{
    int data_put;
    int data_get;
    bool bPutResult;
    unsigned int current_capacity;



    // Put values, check capacity
    bPutResult = CircularBuffer_Put(buffer, 0x1234);
    CHECK_TRUE(bPutResult);
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 9);

    bPutResult = CircularBuffer_Put(buffer, 0x5678);
    CHECK_TRUE(bPutResult);
