// Everything in the test group is available
// to associated test cases
TEST_GROUP(CircularBuffer)
{
    CircularBuffer* buffer = 0;
    const unsigned int capacity = 10;
    const int emptyValue = -1;

    // setup runs before each test
    void setup()
    {
        buffer = CircularBuffer_Create(capacity, emptyValue);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buffer);
    }

};

// PUSH THE TEST BUTTON WHEN YOU START TO WORK
// Pushing the test button saves your work and runs the build.

// each test gets a fresh CurcularBuffer
TEST(CircularBuffer, check_empty_then_not_empty)
{
    bool bTestResult;

    // Should be empty
    bTestResult = CircularBuffer_IsEmpty(buffer);
    CHECK_TRUE(bTestResult);

    // Add data and should no longer be empty
    CircularBuffer_Put(buffer, 0x1234); 
    bTestResult = CircularBuffer_IsEmpty(buffer);
    CHECK_FALSE(bTestResult);

}


TEST(CircularBuffer, check_full_then_not_full)
{
    bool bTestResult;
    unsigned int current_capacity;
    int data; 

    // Should be empty
    bTestResult = CircularBuffer_IsEmpty(buffer);
    CHECK_TRUE(bTestResult);

    // Current cap should be 10
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 10);

    // Put data
    CircularBuffer_Put(buffer, 1);
    CircularBuffer_Put(buffer, 2);
    CircularBuffer_Put(buffer, 3);
    CircularBuffer_Put(buffer, 4);
    CircularBuffer_Put(buffer, 5);
    CircularBuffer_Put(buffer, 6);
    CircularBuffer_Put(buffer, 7);
    CircularBuffer_Put(buffer, 8);
    CircularBuffer_Put(buffer, 9);

    // 9 added so not full
    bTestResult = CircularBuffer_IsFull(buffer);
    CHECK_FALSE(bTestResult);

   current_capacity = CircularBuffer_Capacity(buffer);
   LONGS_EQUAL(current_capacity, 1);

    // Add one more, should be full now
    CircularBuffer_Put(buffer, 10);

   current_capacity = CircularBuffer_Capacity(buffer);
   LONGS_EQUAL(current_capacity, 0);

   bTestResult = CircularBuffer_IsFull(buffer);   
   CHECK_TRUE(bTestResult); 

    // Get data 
    data = CircularBuffer_Get(buffer);  // 9
    data = CircularBuffer_Get(buffer);  // 8
    data = CircularBuffer_Get(buffer);  // 7
    data = CircularBuffer_Get(buffer);  // 6
    data = CircularBuffer_Get(buffer);  // 5
    data = CircularBuffer_Get(buffer);  // 4
    data = CircularBuffer_Get(buffer);  // 3
    data = CircularBuffer_Get(buffer);  // 2
    data = CircularBuffer_Get(buffer);  // 1

    // All but one removed so cap should be 9
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 9);

    // Remove one more so cap should be 10
    data = CircularBuffer_Get(buffer); 
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 10);


    // Try to remove one more get def_val and cap should still be 10
    data = CircularBuffer_Get(buffer);
    LONGS_EQUAL(data, -1);
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 10);

}

