TEST(CircularBuffer, check_capacity_when_putting_and_getting)
{
    int data_put;
    int data_get;
    bool bPutResult;
    unsigned int current_capacity;

    // Current cap should be 10 before anything gets put it
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 10);


    // Put value, check capacity
    bPutResult = CircularBuffer_Put(buffer, 0x1234);
    CHECK_TRUE(bPutResult);
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 9);

    // Put value, check capacity
    bPutResult = CircularBuffer_Put(buffer, 0x5678);
    CHECK_TRUE(bPutResult);
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 8);

    //Get value, check capacity and value
    data_get = CircularBuffer_Get(buffer);
    LONGS_EQUAL(data_get, 0x1234);
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 9);   

    //Get value, check capacity and value
    data_get = CircularBuffer_Get(buffer);
    LONGS_EQUAL(data_get, 0x5678);
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 10); 

    // Getting more than what's there should result in -1
    // and capacity should still be 10 
    data_get = CircularBuffer_Get(buffer);
    LONGS_EQUAL(data_get, -1);       
    current_capacity = CircularBuffer_Capacity(buffer);
    LONGS_EQUAL(current_capacity, 10); 

}


