#include "CircularBuffer.h"

CircularBuffer::CircularBuffer(const unsigned int capacity, const int defaultValue) : buffer(capacity)
{
    maxCapacity = capacity;
    defaultVal = defaultValue;
    head = 0;
    tail = 0;
}

CircularBuffer::~CircularBuffer()
{
}

bool CircularBuffer::isEmpty() const
{
    bool isEmpty;
    if (head == tail)
    {
        isEmpty = true;
    }
    else
    {
        isEmpty = false;
    }
    return isEmpty;
}

bool CircularBuffer::isFull() const
{
    bool isFull;
    if (tail + 1 == head)
    {
        isFull = true;
    }
    else
    {
        isFull = false;
    }
    return isFull;
}

int CircularBuffer::capacity() const
{
    return maxCapacity;
}

int CircularBuffer::get()
{
    int value;

    if (isEmpty() == true)
    {
        value = defaultVal;
    }
    else
    {
        value = buffer[tail];
    }

    return value;
}

void CircularBuffer::put(int value)
{
    buffer[head] = value;
}

