#include "CircularBuffer.h"
#include "stdio.h"

struct CircularBuffer
{
    int* buffer;            // pointer to buffer
    int inIdx;              // input data index
    int outIdx;             // output data index
    unsigned int capacity;  // total capacity
    unsigned int capacity_empty; // empty capacity of the buffer
    int default_value;      // default value to return when buffer empty
};

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{
    struct CircularBuffer * self = (struct CircularBuffer *)calloc(1, sizeof(struct CircularBuffer));

    self->inIdx = 0;
    self->outIdx = 0;
    self->capacity = capacity;
    self->capacity_empty = capacity;
    self->default_value = default_value;

    printf("Create new buffer: cap: %d, def:%d\n", capacity, default_value);

    if(capacity > 0)
    {
        self->buffer = (int *)calloc(capacity, sizeof(int));
    } else
    {
        self->buffer = NULL;
    }
    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * self)
{

    printf("Destroy buffer\n");

    free(self->buffer);
    free(self);
}

bool CircularBuffer_IsEmpty(struct CircularBuffer * self)
{
    bool ret = false;
    if(self->capacity_empty == self->capacity)
    {
        ret = true;
    }
    return ret;
}

bool CircularBuffer_IsFull(struct CircularBuffer * self)
{
    bool ret = false;
    if(self->capacity_empty == 0 || self->capacity == 0)
    {
        ret = true;
    }
    return ret;
}

bool CircularBuffer_Put(struct CircularBuffer * self, int value)
{
    bool ret = true;
    // check if is full
    if(!CircularBuffer_IsFull(self))
    {

        printf("PUT: value:%d; cap_empty:%d; inIdx:%d\n", value, self->capacity_empty, self->inIdx);

        // save new value
        self->buffer[self->inIdx] = value;
        // update inIdx
        if(self->inIdx < self->capacity - 1)
        {
            self->inIdx ++;
        } else
        {
            self->inIdx = 0;
        }
        // update capacity_rest
        self->capacity_empty --;
    } else
    {

        printf("PUT - FULL: value:%d; cap_empty:%d; inIdx:%d\n", value, self->capacity_empty, self->inIdx);

        ret = false;
    }
    // return operation flag
    return ret;
}

int CircularBuffer_Get(struct CircularBuffer * self)
{
    int ret;
    // check empty buffer
    if(!CircularBuffer_IsEmpty(self))
    {
        // get buffered value
        ret = self->buffer[self->outIdx];

        printf("GET: value:%d; cap_empty:%d; outIdx:%d\n", ret, self->capacity_empty, self->outIdx);

        // update outIdx
        if(self->outIdx < self->capacity - 1)
        {
            self->outIdx ++;
        } else
        {
            self->outIdx = 0;
        }
        // update capacity_empty
        self->capacity_empty ++;
    } else
    {

        ret = self->default_value;

        printf("GET - EMPTY: value:%d; cap_empty:%d; outIdx:%d\n", ret, self->capacity_empty, self->outIdx);
    }
    // return get value
    return ret;
}

unsigned int CircularBuffer_Capacity(struct CircularBuffer * self)
{
    return self->capacity;
}

