TEST(CircularBuffer, is_full_flag)
{
    printf("*** is_full_flag ***\n");
    unsigned int capacity = 10;
    int default_value = -1;
    CircularBuffer * dut = NULL;
    bool ret_bool;

    // creat new buffer
    dut = CircularBuffer_Create(capacity, default_value);
    CHECK(dut != NULL);

    for(int i = 0; i < 10; i ++)
    {
        // Check the isFull flag - false
        ret_bool = CircularBuffer_IsFull(dut);
        CHECK(ret_bool == false);

        // fill up the buffer
        ret_bool = CircularBuffer_Put(dut, i);
        CHECK(ret_bool == true);
    }

    // Check the isFull flag - true
    ret_bool = CircularBuffer_IsFull(dut);
    CHECK(ret_bool == true);

    for(int i = 0; i < 10; i ++)
    {
        // Get samples from buffet
        CircularBuffer_Get(dut);

        // Check the isFull flag - false
        ret_bool = CircularBuffer_IsFull(dut);
        CHECK(ret_bool == false);
    }

    // destroy used buffer
    CircularBuffer_Destroy(dut);
}

