#include "CircularBuffer.h"

#define FALSE 0
#define TRUE 1

bool i_bIsEmpty = TRUE;
bool i_bIsFull = FALSE;
unsigned int i_uIndexIn = 0;
unsigned int i_uIndexOut = 0;
unsigned int i_uSize = 0;
int i_nDefaultValue = 0;

struct CircularBuffer
{
    int nElement;
};

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{
    if (capacity == 0)
    {
        return NULL;
    }

    i_uSize = capacity;
    i_nDefaultValue = default_value;

    struct CircularBuffer * self = (struct CircularBuffer *)calloc(capacity, sizeof(struct CircularBuffer));
    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * self)
{
    free(self);
}

bool CircularBuffer_Put(struct CircularBuffer * self, int nElement)
{
    // Check if full
    if (i_bIsFull)
    {
        return FALSE;
    }

    // Save element
    (self + i_uIndexIn)->nElement = nElement;

    // Next index
    i_uIndexIn++;
    if (i_uIndexIn >= i_uSize)
    {
        i_uIndexIn = 0;
    }

    // Check if full
    if (i_uIndexIn == i_uIndexOut)
    {
        i_bIsFull = TRUE;
    }

    // No more empty
    i_bIsEmpty = FALSE;

    return TRUE;
}

int CircularBuffer_Get(struct CircularBuffer * self)
{
    int nElement;

    // Check if empty
    if (i_bIsEmpty)
    {
        return i_nDefaultValue;
    }

    // Get element
    nElement = (self + i_uIndexOut)->nElement;

    // Next index
    i_uIndexOut++;
    if (i_uIndexOut >= i_uSize)
    {
        i_uIndexOut = 0;
    }

    // Check if empty
    if (i_uIndexIn == i_uIndexOut)
    {
        i_bIsEmpty = TRUE;
    }

    // No more full
    i_bIsFull = FALSE;

    return nElement;
}

bool CircularBuffer_IsEmpty(struct CircularBuffer * self)
{
    return i_bIsEmpty;
}

