#include "CppUTest/TestHarness.h"

extern "C"
{
    #include "CircularBuffer.h"
}


// Everything in the test group is available
// to associated test cases
TEST_GROUP(CircularBuffer)
{
    CircularBuffer* buffer = 0;
    const unsigned int capacity = 3;
    const int emptyValue = -1;

    // setup runs before each test
    void setup()
    {
        buffer = CircularBuffer_Create(capacity, emptyValue);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buffer);
    }
};

// PUSH THE TEST BUTTON WHEN YOU START TO WORK
// Pushing the test button saves your work and runs the build.

// each test gets a fresh CircularBuffer

// Size of the buffer = 3 for all test

// Destroy and create
TEST(CircularBuffer, destroy_create)
{
    CircularBuffer_Destroy(buffer);

    // A buffer can not have a size of 0
    buffer = CircularBuffer_Create(0, 0);
    CHECK(buffer == NULL);

    // Check creation
    buffer = CircularBuffer_Create(3, 0);
    CHECK(buffer != NULL);

    // Destroy done in teardown
}

// Put and get, do more as 1 round of the buffer (size of the buffer = 3)
TEST(CircularBuffer, put_get_round)
{
    int nElement;

    #undef NUM
    #define NUM 1
    CircularBuffer_Put(buffer, NUM);
    nElement = CircularBuffer_Get(buffer);
    LONGS_EQUAL(NUM, nElement);

    #undef NUM
    #define NUM 2
    CircularBuffer_Put(buffer, NUM);
    nElement = CircularBuffer_Get(buffer);
    LONGS_EQUAL(NUM, nElement);

    #undef NUM
    #define NUM 3
    CircularBuffer_Put(buffer, NUM);
    nElement = CircularBuffer_Get(buffer);
    LONGS_EQUAL(NUM, nElement);

    #undef NUM
    #define NUM 4
    CircularBuffer_Put(buffer, NUM);
    nElement = CircularBuffer_Get(buffer);
    LONGS_EQUAL(NUM, nElement);

    // Question: what is recommended:
    //  - Sequential test as here
    //  - Using loop, more compact but more complex to undestand
}

// Put and get, fill and empty (size of the buffer = 3)
TEST(CircularBuffer, put_get_fill)
{
    int nElement;

    // First test
    CircularBuffer_Put(buffer, 1);
    CircularBuffer_Put(buffer, 2);

    nElement = CircularBuffer_Get(buffer);
    LONGS_EQUAL(1, nElement);

    // Second test with rollover of the buffer
    CircularBuffer_Put(buffer, 3);
    CircularBuffer_Put(buffer, 4);

    nElement = CircularBuffer_Get(buffer);
    LONGS_EQUAL(2, nElement);
    nElement = CircularBuffer_Get(buffer);
    LONGS_EQUAL(3, nElement);
    nElement = CircularBuffer_Get(buffer);
    LONGS_EQUAL(4, nElement);

    // Question: where must be the assert:
    //  - As here
    //  - Always at the end
}

// Default value when empty
TEST(CircularBuffer, default_value_when_empty)
{
    int nElement;

    nElement = CircularBuffer_Get(buffer);
    LONGS_EQUAL(-1, nElement);
}
