#include "CircularBuffer.h"

#define FALSE 0
#define TRUE 1

struct CircularBuffer
{
    bool bIsEmpty;
    bool bIsFull;
    unsigned int uIndexIn;
    unsigned int uIndexOut;
    unsigned int uSize;
    int nDefaultValue;
    int *pnElement;
};

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{
    if (capacity == 0)
    {
        return NULL;
    }

    int * pnElement = (int *)calloc(capacity, sizeof(int));
    struct CircularBuffer * self = (struct CircularBuffer *)calloc(1, sizeof(struct CircularBuffer));

    self->bIsEmpty = TRUE;
    self->bIsFull = FALSE;
    self->uIndexIn = 0;
    self->uIndexOut = 0;
    self->uSize = capacity;
    self->nDefaultValue = default_value;
    self->pnElement = pnElement;

    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * self)
{
    free(self->pnElement);
    free(self);
}

bool CircularBuffer_Put(struct CircularBuffer * self, int nElement)
{
    // Check if full
    if (self->bIsFull)
    {
        return FALSE;
    }

    // Save element
    *(self->pnElement + self->uIndexIn) = nElement;

    // Next index
    self->uIndexIn++;
    if (self->uIndexIn >= self->uSize)
    {
        self->uIndexIn = 0;
    }

    // Check if full
    if (self->uIndexIn == self->uIndexOut)
    {
        self->bIsFull = TRUE;
    }

    // No more empty
    self->bIsEmpty = FALSE;

    return TRUE;
}

int CircularBuffer_Get(struct CircularBuffer * self)
{
    int nElement;

    // Check if empty
    if (self->bIsEmpty)
    {
        return self->nDefaultValue;
    }

    // Get element
    nElement = *(self->pnElement + self->uIndexOut);

    // Next index
    self->uIndexOut++;
    if (self->uIndexOut >= self->uSize)
    {
        self->uIndexOut = 0;
    }

    // Check if empty
    if (self->uIndexIn == self->uIndexOut)
    {
        self->bIsEmpty = TRUE;
    }

    // No more full
    self->bIsFull = FALSE;

    return nElement;
}

bool CircularBuffer_IsEmpty(struct CircularBuffer * self)
{
    return self->bIsEmpty;
}

bool CircularBuffer_IsFull(struct CircularBuffer * self)
{
    return self->bIsFull;
}

unsigned int CircularBuffer_Capacity(struct CircularBuffer *self)
{
    return 0;
}

