#include "CppUTest/TestHarness.h"

extern "C"
{
    #include "CircularBuffer.h"
}

static const int test_array[] = { -9034, -4022, -9131,  7105,  4946, 
                                   4529, -1297,  2222, -4633, -2000,
                                  -1703,  8150,  4660 };

/* Let's test a circular buffer of length zero.
   The interface specification does not forbid setting
   capacity to zero, so I decided to allow it. */
TEST_GROUP(CapacityZero)
{
    CircularBuffer* buffer = 0;
    const unsigned int capacity = 0;
    const int emptyValue = -1;

    // setup runs before each test
    void setup()
    {
        buffer = CircularBuffer_Create(capacity, emptyValue);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buffer);
    }
};

TEST(CapacityZero, test_buffer_is_created)
{
    LONGS_EQUAL(capacity, 0);
    LONGS_EQUAL(emptyValue, -1);
    CHECK(buffer != 0);
}

TEST(CapacityZero, test_buffer_is_empty)
{
    // A circular buffer of capacity 0 is always empty.
    LONGS_EQUAL(capacity, 0);
    LONGS_EQUAL(true, CircularBuffer_IsEmpty(buffer));
}

TEST(CapacityZero, test_buffer_is_full)
{
    // A circular buffer of capacity 0 is always full.
    LONGS_EQUAL(capacity, 0);
    LONGS_EQUAL(true, CircularBuffer_IsFull(buffer));
}

TEST(CapacityZero, test_put)
{
    // Put shall always fail.
    LONGS_EQUAL(capacity, 0);
    LONGS_EQUAL(true, CircularBuffer_Put(buffer, test_array[2]));
}
