#include "CppUTest/TestHarness.h"

extern "C"
{
    #include "CircularBuffer.h"
}

static const int test_array[] = { -9034, -4022, -9131,  7105,  4946, 
                                   4529, -1297,  2222, -4633, -2000,
                                  -1703,  8150,  4660 };

/* =======================================================
   Let's test a circular buffer of capacity zero.
   The interface specification does not forbid setting
   capacity to zero, so I decided to allow it. 
   ======================================================= */
TEST_GROUP(CapacityZero)
{
    CircularBuffer     * buff_0      = 0;
    const unsigned int   cap_0       = 0;
    const int            empty_val_0 = -14;

    // setup runs before each test
    void setup()
    {
        buff_0 = CircularBuffer_Create(cap_0, empty_val_0);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buff_0);
    }
};

TEST(CapacityZero, test_CAP_0_is_created)
{
    LONGS_EQUAL(cap_0, 0);
    CHECK(buff_0 != 0);
}

TEST(CapacityZero, test_CAP_0_capacity)
{
    LONGS_EQUAL(cap_0, 0);
    LONGS_EQUAL(cap_0, CircularBuffer_Capacity(buff_0));
}

TEST(CapacityZero, test_CAP_0_is_empty)
{
    // A circular buffer of capacity 0 is always empty.
    LONGS_EQUAL(cap_0, 0);
    LONGS_EQUAL(true, CircularBuffer_IsEmpty(buff_0));
}

TEST(CapacityZero, test_CAP_0_is_full)
{
    // A circular buffer of capacity 0 is always full.
    LONGS_EQUAL(cap_0, 0);
    LONGS_EQUAL(true, CircularBuffer_IsFull(buff_0));
}

TEST(CapacityZero, test_CAP_0_put)
{
    // Put shall always fail.
    LONGS_EQUAL(cap_0, 0);
    LONGS_EQUAL(false, CircularBuffer_Put(buff_0, test_array[2]));
}

TEST(CapacityZero, test_CAP_0_get)
{
    // Get shall always return the default value.
    LONGS_EQUAL(cap_0, 0);
    LONGS_EQUAL(empty_val_0, CircularBuffer_Get(buff_0));
    LONGS_EQUAL(empty_val_0, CircularBuffer_Get(buff_0));
    LONGS_EQUAL(empty_val_0, CircularBuffer_Get(buff_0));
}

/* =======================================================
   Let's test a circular buffer of capacity 1. 
   =======================================================*/
TEST_GROUP(CapacityOne)
{
    CircularBuffer     * buff_1      = 0;
    const unsigned int   cap_1       = 1;
    const int            empty_val_1 = 23;

    // setup runs before each test
    void setup()
    {
        buff_1 = CircularBuffer_Create(cap_1, empty_val_1);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buff_1);
    }
};

TEST(CapacityOne, test_CAP_1_is_created)
{
    LONGS_EQUAL(cap_1, 1);
    CHECK(buff_1 != 0);
}

TEST(CapacityOne, test_CAP_1_capacity)
{
    LONGS_EQUAL(cap_1, 1);
    LONGS_EQUAL(cap_1, CircularBuffer_Capacity(buff_1));
}

TEST(CapacityOne, test_CAP_1_is_empty)
{
    LONGS_EQUAL(cap_1, 1);
    LONGS_EQUAL(true, CircularBuffer_IsEmpty(buff_1));
}

TEST(CapacityOne, test_CAP_1_is_full)
{
    LONGS_EQUAL(cap_1, 1);
    LONGS_EQUAL(false, CircularBuffer_IsFull(buff_1));
}
