#include "CircularBuffer.h"
#include <stdio.h>

struct CircularBuffer
{
    // Buffer capacity, i.e. how many int can fit in
    unsigned int capacity;

    // Pointer to the buffer itself which is an array whose length
    // equals the member "capacity"
    int * buffer;

    unsigned int remaining_capacity;
};

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{
    struct CircularBuffer * self = NULL;

    if (capacity > 0) 
    {
        self = (struct CircularBuffer *)calloc(1, sizeof(struct CircularBuffer));

        self->capacity = capacity;
        self->buffer = (int *) calloc(self->capacity, sizeof(int));

        self->remaining_capacity = capacity;
    }

    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * self)
{
    free(self->buffer);
    free(self);

    // free() doesn't change the pointer value, 
    // we defensively set it to NULL so it doesn't pass NULL-checks anymore
    self = NULL;
}

bool CircularBuffer_IsEmpty(struct CircularBuffer * self)
{
    return self->remaining_capacity == self->capacity;
}

bool CircularBuffer_IsFull(struct CircularBuffer * self)
{
    return self->remaining_capacity == 0;
}

bool CircularBuffer_Put(struct CircularBuffer * self, int element)
{
