#include <stdio.h>


// Everything in the test group is available
// to associated test cases
TEST_GROUP(CircularBuffer)
{
    CircularBuffer* buffer = 0;
    const unsigned int capacity = 10;
    const int emptyValue = -1;

    // setup runs before each test
    void setup()
    {
        buffer = CircularBuffer_Create(capacity, emptyValue);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buffer);
    }

};

// PUSH THE TEST BUTTON WHEN YOU START TO WORK
// Pushing the test button saves your work and runs the build.

/**
* @brief Test the creation of a circular buffer
* In this test we don't use the cicular buffer created in setup() 
* because we want to manage both its creation and destruction
*/
TEST(CircularBuffer, create)
{
    CircularBuffer * new_buffer = NULL;
    const unsigned int BUFFER_CAPACITY = 10;
    const int BUFFER_DEFAULT_VALUE = 0;

    new_buffer = CircularBuffer_Create(BUFFER_CAPACITY, BUFFER_DEFAULT_VALUE);

    // Checks that the buffer objects has now a valid address
    CHECK(new_buffer != NULL);

    CircularBuffer_Destroy(new_buffer);

    // For some reason this check doesn't work. C vs C++ issue ? 
    // I don't understand and will ask during the training 
    // CHECK(new_buffer == NULL);
}

/**
* @brief Test that the creation of a circular buffer fails if capacity is 0
* In this test we don't use the cicular buffer created in setup() 
* because we want to manage its creation parameter
*/
TEST(CircularBuffer, create_with_zero_capacity)
{
    CircularBuffer * new_buffer = NULL;
    const unsigned int BUFFER_CAPACITY = 0;
    const int BUFFER_DEFAULT_VALUE = 0;

    new_buffer = CircularBuffer_Create(BUFFER_CAPACITY, BUFFER_DEFAULT_VALUE);

    // Since the capcity is 0, the buffer creation must have failed
    CHECK(new_buffer == NULL);
}

/**
* @brief Test that a newly created buffer is empty
*/
TEST(CircularBuffer, new_buffer_is_empty)
{
    CHECK_TRUE(CircularBuffer_IsEmpty(buffer));
}

/**
* @brief Test that a newly created buffer is not full
*/
TEST(CircularBuffer, new_buffer_is_not_full)
{
    CHECK_FALSE(CircularBuffer_IsFull(buffer));
}

/**
* @brief Test that CircularBuffer_Put returns true if we use it to
* fill the whole buffer capacity
*/
TEST(CircularBuffer, fill_buffer_with_put)
{
    int dummy_value = 0; // Incrementing value to fill the buffer with

    for (dummy_value = 0; dummy_value < capacity; dummy_value++)
    {
        CHECK_TRUE(CircularBuffer_Put(buffer, dummy_value));
    }
}

