#include "CircularBuffer.h"

struct CircularBuffer
{
    int * buffer;
    unsigned int capacity;
    unsigned int inIndex;
    unsigned int outIndex;
    unsigned int freeSpace;
    int defaultValue;
};

struct CircularBuffer * CircularBuffer_Create(unsigned int capacity, int default_value)
{
    struct CircularBuffer * self = NULL;

    if(capacity > 0)
    {
        self = (struct CircularBuffer *)calloc(1, sizeof(struct CircularBuffer));

        self->capacity  = capacity;
        self->defaultValue = default_value;
        self->inIndex   = 0;
        self->outIndex  = 0;
        self->freeSpace = capacity;


        self->buffer = (int *)calloc(self->capacity, sizeof(int));
    }

    return self;
}

void CircularBuffer_Destroy(struct CircularBuffer * self)
{
    free(self->buffer);
    free(self);
}

bool CircularBuffer_IsEmpty(struct CircularBuffer * self)
{
    return (self->freeSpace == self->capacity ? true : false); 
}

bool CircularBuffer_IsFull(struct CircularBuffer * self)
{
    return (self->freeSpace == 0 ? true : false); 
}

bool CircularBuffer_Put(struct CircularBuffer * self , int value)
{
    bool result = false;

    if(self->freeSpace)
    {
        self->buffer[self->inIndex] = value;
        self->freeSpace--;
        self->inIndex = ((self->inIndex + 1) % self->capacity);
        result = true;
    }

    return result;
}

int CircularBuffer_Get(struct CircularBuffer * self)
{
    int itemToReturn = self->defaultValue;

    if(self->freeSpace < self->capacity)
    {
        itemToReturn = self->buffer[self->outIndex];
        self->freeSpace++;
        self->outIndex = ((self->outIndex + 1) % self->capacity);
    }

    return itemToReturn;
}

unsigned int CircularBuffer_Capacity(struct CircularBuffer * self)
{
    return self->capacity;
}

