#include "CppUTest/TestHarness.h"

extern "C"
{
    #include "CircularBuffer.h"
}


// Everything in the test group is available
// to associated test cases
TEST_GROUP(CircularBuffer)
{
    CircularBuffer* buffer = 0;
    const unsigned int capacity = 10;
    const int emptyValue = -1;

    // setup runs before each test
    void setup()
    {
        buffer = CircularBuffer_Create(capacity, emptyValue);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buffer);
    }

};

// PUSH THE TEST BUTTON WHEN YOU START TO WORK
// Pushing the test button saves your work and runs the build.

// Test to create a buffer with non-0 capacity
TEST(CircularBuffer, create_with_non_zero_capacity)
{
    // Data to test create function
    CircularBuffer* create_buffer = 0;
    unsigned int create_capacity = 10;
    int create_emptyValue = -1;

    create_buffer = CircularBuffer_Create(create_capacity, create_emptyValue);

    CHECK(create_buffer != 0);

    CircularBuffer_Destroy(create_buffer);
}

// Test to create a buffer with 0 capacity
TEST(CircularBuffer, create_with_zero_capacity)
{
    // Data to test create function
    CircularBuffer* create_buffer = 0;
    unsigned int create_capacity = 0;
    int create_emptyValue = -1;

    create_buffer = CircularBuffer_Create(create_capacity, create_emptyValue);

    CHECK(create_buffer == 0);

}

// Test is empty function with an empty buffer
TEST(CircularBuffer, is_empty_with_empty_buffer)
{
    CHECK_TRUE(CircularBuffer_IsEmpty(buffer));
}


// Test is empty function with a non-empty buffer
TEST(CircularBuffer, is_empty_with_non_empty_buffer)
{
    CircularBuffer_Put(buffer, 27);
    CHECK_FALSE(CircularBuffer_IsEmpty(buffer));
}

// Test is empty function with a just emptied buffer
TEST(CircularBuffer, is_empty_with_emptied_buffer)
{
    //Fill and empty a buffer
    CircularBuffer_Put(buffer, 27);
    CircularBuffer_Get(buffer);

    CHECK_TRUE(CircularBuffer_IsEmpty(buffer));
}

// Test put function with a positive number
TEST(CircularBuffer, put_pos_number)
{
    //Put 10 items
    CHECK_TRUE(CircularBuffer_Put(buffer, 0));
    CHECK_TRUE(CircularBuffer_Put(buffer, 1));
    CHECK_TRUE(CircularBuffer_Put(buffer, 2));
    CHECK_TRUE(CircularBuffer_Put(buffer, 3));
    CHECK_TRUE(CircularBuffer_Put(buffer, 4));
    CHECK_TRUE(CircularBuffer_Put(buffer, 5));
    CHECK_TRUE(CircularBuffer_Put(buffer, 6));
    CHECK_TRUE(CircularBuffer_Put(buffer, 7));
    CHECK_TRUE(CircularBuffer_Put(buffer, 8));
    CHECK_TRUE(CircularBuffer_Put(buffer, 9));
    //Full -> Not able to put one more
    CHECK_FALSE(CircularBuffer_Put(buffer, 10));
}

// Test get function
TEST(CircularBuffer, get_item)
{
    //Put 10 items
    const int valueToGet_pos = 27;
    const int valueToGet_neg = -27;

    //Put 2 items inside buffer
    CircularBuffer_Put(buffer, valueToGet_pos);
    CircularBuffer_Put(buffer, valueToGet_neg);

    //Get values put previously    
    LONGS_EQUAL(valueToGet_pos, CircularBuffer_Get(buffer));
    LONGS_EQUAL(valueToGet_neg, CircularBuffer_Get(buffer));
}

