#include "CppUTest/TestHarness.h"

extern "C"
{
    #include "CircularBuffer.h"
}


// Everything in the test group is available
// to associated test cases
TEST_GROUP(CircularBuffer)
{
    CircularBuffer* buffer = 0;
    const unsigned int capacity = 10;
    const int emptyValue = -1;

    // setup runs before each test
    void setup()
    {
        buffer = CircularBuffer_Create(capacity, emptyValue);
    }

    // teardown runs after each test
    void teardown()
    {
        CircularBuffer_Destroy(buffer);
    }

};

// *********************************************************************************************
// Tests related to the constructor
// *********************************************************************************************** 
typedef struct TestVectorCapacity
{
    const unsigned int capacity;
    const unsigned int expCapacity;
}TestVectorCapacity;

// Test that the capacity is the one given to the constructor
TEST(CircularBuffer, capacity)
{
    TestVectorCapacity testVector[] =
    {
        {1,   1},
        {10,  10},
        {100, 100},
        {452, 452},
        {9634, 9634},
        {0,     0},
    };
    const int DEFAULT_BUFFER_VALUE = -1;
    CircularBuffer* myBuffer = 0;

    const unsigned int tableSize = sizeof(testVector)/sizeof(testVector[0]);

    // Test that the Circular Buffer has the good sized
    for(int i = 0; i < tableSize; ++i)
    {
        myBuffer = CircularBuffer_Create(testVector[i][0], DEFAULT_BUFFER_VALUE);
        LONGS_EQUAL(capacity, CircularBuffer_Capacity(myBuffer));
    }
}

// Each test in a group must have a unique name
// Make as many tests as you like
TEST(CircularBuffer, test_with_all_the_macros_you_should_need_for_this_exercise)
{
    LONGS_EQUAL(capacity, 10);
    LONGS_EQUAL(emptyValue, -1);
    CHECK(buffer != 0);
    CHECK(true);
    CHECK_TRUE(true);
    CHECK_FALSE(false);
