{
}

TEST(CircularBuffer, isEmptyAfterCreate)
{
    CHECK_TRUE(CircularBuffer_IsEmpty(buffer));
}

TEST(CircularBuffer, notFullAfterCreate)
{
    CHECK_FALSE(CircularBuffer_IsFull(buffer));
}

